	@Test
	public void testGetExtendedMetadata() throws CustomFault {
		testGetMetadata();
		
		GetExtendedMetadata parameters = new GetExtendedMetadata();
		parameters.setId("999900001:2015-05-31T21:00:00-0400|short");
		GetExtendedMetadataResponse response = service.getExtendedMetadata(parameters);
		
		MediaMetadata mmd = response.getGetExtendedMetadataResult().getMediaMetadata();
		assertEquals("999900001:2015-05-31T21:00:00-0400|short", mmd.getId());
		assertEquals(ItemType.TRACK, mmd.getItemType());		
		assertEquals("audio/mp3", mmd.getMimeType());
		assertEquals("Hourly Newscast", mmd.getTitle());
		assertEquals("0", mmd.getDynamic().getProperty().get(0).getValue());
	}

