	@Test
	public void testRateItem() throws CustomFault {
		testGetMetadata();
		
		GetExtendedMetadata parameters1 = new GetExtendedMetadata();
		parameters1.setId("411044416:411044417");
		GetExtendedMetadataResponse response1 = service.getExtendedMetadata(parameters1);
		assertEquals("0", response1.getGetExtendedMetadataResult().getMediaMetadata().getDynamic().getProperty().get(0).getValue());
		
		RateItem parameters2 = new RateItem();
		parameters2.setId("411044416:411044417");
		RateItemResponse response2 = service.rateItem(parameters2);
		assertFalse(response2.getRateItemResult().isShouldSkip());
		
		GetExtendedMetadata parameters3 = new GetExtendedMetadata();
		parameters3.setId("411044416:411044417");
		GetExtendedMetadataResponse response3 = service.getExtendedMetadata(parameters3);
		assertEquals("1", response3.getGetExtendedMetadataResult().getMediaMetadata().getDynamic().getProperty().get(0).getValue());						
	}

